"use client";

import { useEffect, useState } from "react";
import { useSession } from "next-auth/react";
import { useRouter } from "next/navigation";
import Link from "next/link";

interface Post {
  id: string;
  title: string;
  content?: string;
  excerpt?: string;
  tags: string[];
  createdAt: string;
  author?: { name: string | null; email: string | null };
}

const TAG_COLORS: Record<string, string> = {
  "AI": "#d4877a", "思考": "#8a7a6b", "コード": "#6b7a8a",
  "3D": "#8a6b7a", "哲学": "#7a6b8a", "テクノロジー": "#d4877a",
  "社会": "#6b8a7a", "作品": "#7a8a6b", "ツール": "#6b8a8a",
};

function fmtDate(d: string) {
  if (!d) return "";
  try {
    const date = new Date(d);
    return new Intl.DateTimeFormat("ja-JP", {
      timeZone: "Asia/Tokyo",
      year: "numeric",
      month: "2-digit",
      day: "2-digit",
    }).format(date).replace(/\//g, ".");
  } catch {
    return d.substring(0, 10).replace(/-/g, ".");
  }
}

export default function HomePage() {
  const { data: session } = useSession();
  const router = useRouter();
  const [posts, setPosts] = useState<Post[]>([]);
  const [overlayOpen, setOverlayOpen] = useState(false);
  const [overlayContent, setOverlayContent] = useState("");
  const [overlayMeta, setOverlayMeta] = useState({ date: "", tags: [] as string[], author: "" });
  const [searchQuery, setSearchQuery] = useState("");

  useEffect(() => {
    fetch("/api/posts")
      .then((r) => r.json())
      .then((data) => {
        setPosts(data.map((p: Post) => ({
          ...p,
          excerpt: p.excerpt || (p.content ? p.content.replace(/<[^>]*>/g, "").substring(0, 120) + "..." : ""),
        })));
      })
      .catch(console.error);
  }, []);

  const openPost = (post: Post) => {
    setOverlayMeta({
      date: fmtDate(post.createdAt),
      tags: post.tags || [],
      author: post.author?.name || "",
    });
    setOverlayContent(post.content || "<p>記事の内容がありません。</p>");
    setOverlayOpen(true);
    document.body.style.overflow = "hidden";
  };

  const closeOverlay = () => {
    setOverlayOpen(false);
    document.body.style.overflow = "";
  };

  useEffect(() => {
    const handler = (e: KeyboardEvent) => { if (e.key === "Escape") closeOverlay(); };
    document.addEventListener("keydown", handler);
    return () => document.removeEventListener("keydown", handler);
  }, []);

  const blogPosts = posts.filter((p) => !p.tags?.includes("product"));
  const productPosts = posts.filter((p) => p.tags?.includes("product"));

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      router.push(`/user/${encodeURIComponent(searchQuery.trim())}`);
    }
  };

  return (
    <>
      {/* ─── Navbar ─── */}
      <nav className="navbar" id="navbar" style={{ justifyContent: "space-between" }}>
        <Link href="/" className="nav-logo" style={{ textDecoration: "none" }}>
          <img src="/images/a.png" alt="Next Blog" className="nav-logo-img" />
          Next Blog
        </Link>
        <div className="nav-auth">
          {session ? (
            <>
              <Link href="/editor" className="nav-auth-btn nav-write-btn">✏ 記事を書く</Link>
              <Link href={`/user/${session.user?.name || ""}`} className="nav-auth-btn nav-user-btn" style={{ textDecoration: "none" }}>
                マイページ
              </Link>
              <Link href="/settings" className="nav-auth-btn nav-user-btn" style={{ textDecoration: "none" }}>⚙</Link>
            </>
          ) : (
            <Link href="/login" className="nav-auth-btn nav-login-btn">ログイン</Link>
          )}
        </div>
      </nav>

      {/* ─── Hero ─── */}
      <div className="main-content">
        <section style={{
          textAlign: "center",
          padding: "100px 20px 60px",
        }}>
          <h1 style={{
            fontFamily: "var(--serif)",
            fontSize: "clamp(32px, 5vw, 48px)",
            fontWeight: 300,
            color: "var(--azuki-deep)",
            marginBottom: 12,
            letterSpacing: "0.06em",
          }}>
            Next Blog
          </h1>
          <p style={{
            fontSize: 15,
            color: "var(--text-soft)",
            maxWidth: 420,
            margin: "0 auto",
            lineHeight: 1.8,
          }}>
            思考と創造を共有するプラットフォーム。
          </p>

          <form onSubmit={handleSearch} style={{ marginTop: 32, display: "flex", justifyContent: "center", gap: 8 }}>
            <input
              type="text"
              placeholder="ユーザーを検索..."
              className="login-input"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              style={{ width: 280, marginBottom: 0, border: "1px solid var(--border)", background: "var(--bg-card)" }}
            />
            <button type="submit" className="editor-btn editor-btn-primary" style={{ padding: "0 24px" }}>
              検索
            </button>
          </form>
        </section>

        <div className="section-divider" />

        {/* ─── Recent Blog Posts ─── */}
        <section className="section">
          <h2 className="section-title">最近の記事</h2>
          {blogPosts.length === 0 ? (
            <p style={{ textAlign: "center", color: "var(--text-soft)", padding: "40px 0" }}>
              まだ記事がありません。ログインして最初の記事を書きましょう。
            </p>
          ) : (
            <div className="blog-list">
              {blogPosts.slice(0, 10).map((p) => (
                <div key={p.id} className="blog-item" onClick={() => openPost(p)} style={{ cursor: "pointer" }}>
                  <div style={{ flex: 1 }}>
                    <div style={{ display: "flex", alignItems: "center", gap: 12, marginBottom: 6, flexWrap: "wrap" }}>
                      <h3 style={{ margin: 0 }}>{p.title}</h3>
                      <div style={{ display: "flex", gap: 6, flexWrap: "wrap" }}>
                        {(p.tags || []).filter(t => t !== "product").map((t) => {
                          const c = TAG_COLORS[t] || "#9b6b6b";
                          return <span key={t} className="tag" style={{ color: c, background: c + "18", border: `1px solid ${c}30` }}>{t}</span>;
                        })}
                      </div>
                    </div>
                    <p>{p.excerpt}</p>
                    <div style={{ fontSize: 12, color: "var(--azuki-light)", marginTop: 8 }}>
                      by{" "}
                      <Link
                        href={`/user/${p.author?.name || ""}`}
                        style={{ color: "var(--azuki)", textDecoration: "none" }}
                        onClick={(e) => e.stopPropagation()}
                      >
                        {p.author?.name || "Anonymous"}
                      </Link>
                    </div>
                  </div>
                  <div className="blog-date">{fmtDate(p.createdAt)}</div>
                </div>
              ))}
            </div>
          )}
        </section>

        {/* ─── Products ─── */}
        {productPosts.length > 0 && (
          <>
            <div className="section-divider" />
            <section className="section">
              <h2 className="section-title">プロダクト</h2>
              <div className="product-grid">
                {productPosts.slice(0, 8).map((p) => (
                  <div key={p.id} className="product-card" onClick={() => openPost(p)} style={{ cursor: "pointer" }}>
                    <div className="product-thumb" style={{ background: "linear-gradient(135deg,#e8d5d0,#e8d5d088)" }}>
                      <div className="product-thumb-inner" />
                    </div>
                    <div className="product-info">
                      <h3>{p.title}</h3>
                      <p>{p.excerpt}</p>
                      <div style={{ fontSize: 11, color: "var(--azuki-light)", marginTop: 6 }}>
                        by {p.author?.name || "Anonymous"}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </section>
          </>
        )}
      </div>

      {/* ─── Footer ─── */}
      <footer className="footer">
        <span className="footer-copy">© 2026 Next Blog</span>
      </footer>

      {/* ─── Overlay ─── */}
      <div className={`post-overlay ${overlayOpen ? "open" : ""}`} onClick={closeOverlay}>
        <div className="post-panel" onClick={(e) => e.stopPropagation()}>
          <div className="post-panel-header">
            <div style={{ display: "flex", gap: 12, alignItems: "center" }}>
              <span style={{ fontSize: 13, color: "var(--text-soft)" }}>{overlayMeta.date}</span>
              {overlayMeta.author && (
                <span style={{ fontSize: 12, color: "var(--azuki-light)" }}>by {overlayMeta.author}</span>
              )}
            </div>
            <button className="post-close-btn" onClick={closeOverlay}>
              <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2"><line x1="18" y1="6" x2="6" y2="18" /><line x1="6" y1="6" x2="18" y2="18" /></svg>
            </button>
          </div>
          <div className="post-panel-body">
            {overlayMeta.tags.length > 0 && (
              <div className="post-meta">
                {overlayMeta.tags.filter(t => t !== "product").map((t) => {
                  const c = TAG_COLORS[t] || "#9b6b6b";
                  return <span key={t} className="tag" style={{ color: c, background: c + "18", border: `1px solid ${c}30` }}>{t}</span>;
                })}
              </div>
            )}
            <div className="md-content" dangerouslySetInnerHTML={{ __html: overlayContent }} />
          </div>
        </div>
      </div>
    </>
  );
}

"use client";

import { useEffect, useState } from "react";
import { useSession, signOut } from "next-auth/react";
import { useRouter } from "next/navigation";
import Link from "next/link";

interface SocialLink {
    label: string;
    url: string;
}

interface Post {
    id: string;
    title: string;
    tags: string[];
    pinned: boolean;
    published: boolean;
}

export default function SettingsPage() {
    const { data: session, status } = useSession();
    const router = useRouter();

    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [image, setImage] = useState("");
    const [headerImage, setHeaderImage] = useState("");
    const [bio, setBio] = useState("");
    const [aboutMe, setAboutMe] = useState("");
    const [links, setLinks] = useState<SocialLink[]>([]);
    const [myPosts, setMyPosts] = useState<Post[]>([]);
    const [saving, setSaving] = useState(false);
    const [message, setMessage] = useState("");

    useEffect(() => {
        if (status === "unauthenticated") router.push("/login");
    }, [status, router]);

    useEffect(() => {
        if (session) {
            setEmail(session.user?.email || "");
            fetch("/api/user/settings")
                .then((r) => r.json())
                .then((data) => {
                    setName(data.name || "");
                    setImage(data.image || "");
                    setHeaderImage(data.headerImage || "");
                    setBio(data.bio || "");
                    setAboutMe(data.aboutMe || "");
                    setLinks(data.links || []);
                })
                .catch(() => { });
            fetch("/api/posts/my")
                .then((r) => r.json())
                .then((posts) => setMyPosts(posts))
                .catch(() => { });
        }
    }, [session]);

    const handleSave = async () => {
        setSaving(true);
        setMessage("");
        try {
            const res = await fetch("/api/user/settings", {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ name, bio, aboutMe, links, image, headerImage }),
            });
            if (res.ok) setMessage("✅ 設定を保存しました。");
            else setMessage("❌ 保存に失敗しました。");
        } catch {
            setMessage("❌ エラーが発生しました。");
        } finally {
            setSaving(false);
        }
    };

    const addLink = () => {
        if (links.length >= 5) return;
        setLinks([...links, { label: "", url: "" }]);
    };

    const updateLink = (i: number, field: "label" | "url", value: string) => {
        const updated = [...links];
        updated[i][field] = value;
        setLinks(updated);
    };

    const removeLink = (i: number) => {
        setLinks(links.filter((_, idx) => idx !== i));
    };

    const handleDeleteAccount = async () => {
        const confirm1 = confirm("本当にアカウントを削除しますか？この操作は取り消せません。");
        if (!confirm1) return;
        const confirm2 = confirm("すべての投稿データも削除されます。本当に削除しますか？");
        if (!confirm2) return;

        try {
            const res = await fetch("/api/user/settings", { method: "DELETE" });
            if (res.ok) {
                await signOut({ callbackUrl: "/" });
            } else {
                setMessage("❌ アカウント削除に失敗しました。");
            }
        } catch {
            setMessage("❌ エラーが発生しました。");
        }
    };

    const togglePinned = async (post: Post) => {
        const isPinned = !post.pinned;
        if (isPinned) {
            const currentlyPinned = myPosts.filter((p: any) => !!p.pinned && !!p.published);
            if (currentlyPinned.length >= 5) {
                setMessage("❌ おすすめは最大5つまでです");
                return;
            }
        }

        setMyPosts(myPosts.map(p => p.id === post.id ? { ...p, pinned: isPinned } : p));
        try {
            await fetch(`/api/posts/${post.id}`, {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ pinned: isPinned })
            });
        } catch { }
    };

    if (status === "loading") {
        return (
            <div className="login-container">
                <div className="login-card" style={{ textAlign: "center" }}>
                    <p style={{ color: "var(--text-soft)" }}>読み込み中...</p>
                </div>
            </div>
        );
    }

    if (!session) return null;

    return (
        <>
            <nav className="navbar" style={{ justifyContent: "center" }}>
                <Link href="/" className="nav-logo" style={{ textDecoration: "none" }}>
                    <img src="/images/a.png" alt="Next Blog" className="nav-logo-img" />
                    Next Blog
                </Link>
            </nav>

            <div className="editor-container" style={{ maxWidth: 600 }}>
                <h1 style={{ fontFamily: "var(--serif)", fontSize: 28, fontWeight: 400, marginBottom: 32 }}>設定</h1>

                {message && (
                    <div className={`login-message ${message.startsWith("❌") ? "login-error" : ""}`} style={{ marginBottom: 20 }}>
                        {message}
                    </div>
                )}

                {/* プロフィール */}
                <section style={{ marginBottom: 40 }}>
                    <h2 className="settings-section-title">プロフィール</h2>

                    <label className="settings-label">ユーザー名</label>
                    <input type="text" className="login-input" value={name} onChange={(e) => setName(e.target.value)} placeholder="ユーザー名" style={{ marginBottom: 16 }} />

                    <label className="settings-label">プロフィール画像</label>
                    <div style={{ display: "flex", alignItems: "center", gap: 16, marginBottom: 16 }}>
                        <div style={{
                            width: 60, height: 60, borderRadius: "50%", background: "var(--bg-soft)",
                            display: "flex", alignItems: "center", justifyContent: "center", overflow: "hidden", border: "1px solid var(--border)"
                        }}>
                            {image ? <img src={image} alt="Profile" style={{ width: "100%", height: "100%", objectFit: "cover" }} /> : <span style={{ color: "var(--text-soft)", fontSize: 24 }}>👤</span>}
                        </div>
                        <label className="editor-btn editor-btn-secondary" style={{ cursor: "pointer", fontSize: 13, padding: "6px 14px" }}>
                            画像を選択 (最大4.5MB)
                            <input
                                type="file"
                                accept="image/*"
                                style={{ display: "none" }}
                                onChange={async (e) => {
                                    const file = e.target.files?.[0];
                                    if (!file) return;
                                    if (file.size > 4.5 * 1024 * 1024) {
                                        setMessage("❌ 4.5MB以下の画像を選択してください");
                                        return;
                                    }
                                    setMessage("画像をアップロード中...");
                                    const formData = new FormData();
                                    formData.append("file", file);
                                    try {
                                        const res = await fetch("/api/upload", { method: "POST", body: formData });
                                        if (res.ok) {
                                            const data = await res.json();
                                            setImage(data.url);
                                            setMessage("");
                                        } else {
                                            const err = await res.json();
                                            setMessage("❌ " + (err.error || "アップロード失敗"));
                                        }
                                    } catch (err) {
                                        setMessage("❌ 予期せぬエラーが発生しました");
                                    } finally {
                                        e.target.value = "";
                                    }
                                }}
                            />
                        </label>
                    </div>

                    <label className="settings-label">ヘッダー画像</label>
                    <div style={{ display: "flex", alignItems: "center", gap: 16, marginBottom: 16 }}>
                        <div style={{
                            width: 120, height: 40, borderRadius: 8, background: "var(--bg-soft)",
                            display: "flex", alignItems: "center", justifyContent: "center", overflow: "hidden", border: "1px solid var(--border)"
                        }}>
                            {headerImage ? <img src={headerImage} alt="Header" style={{ width: "100%", height: "100%", objectFit: "cover" }} /> : <span style={{ color: "var(--text-soft)", fontSize: 12 }}>未設定</span>}
                        </div>
                        <label className="editor-btn editor-btn-secondary" style={{ cursor: "pointer", fontSize: 13, padding: "6px 14px" }}>
                            画像を選択 (最大4.5MB)
                            <input
                                type="file"
                                accept="image/*"
                                style={{ display: "none" }}
                                onChange={async (e) => {
                                    const file = e.target.files?.[0];
                                    if (!file) return;
                                    if (file.size > 4.5 * 1024 * 1024) {
                                        setMessage("❌ 4.5MB以下の画像を選択してください");
                                        return;
                                    }
                                    setMessage("ヘッダー画像をアップロード中...");
                                    const formData = new FormData();
                                    formData.append("file", file);
                                    try {
                                        const res = await fetch("/api/upload", { method: "POST", body: formData });
                                        if (res.ok) {
                                            const data = await res.json();
                                            setHeaderImage(data.url);
                                            setMessage("");
                                        } else {
                                            const err = await res.json();
                                            setMessage("❌ " + (err.error || "アップロード失敗"));
                                        }
                                    } catch (err) {
                                        setMessage("❌ 予期せぬエラーが発生しました");
                                    } finally {
                                        e.target.value = "";
                                    }
                                }}
                            />
                        </label>
                    </div>

                    <label className="settings-label">メールアドレス</label>
                    <input type="email" className="login-input" value={email} disabled style={{ marginBottom: 4, opacity: 0.6 }} />
                    <p style={{ fontSize: 11, color: "var(--azuki-light)", marginBottom: 16 }}>メールアドレスは変更できません</p>

                    <label className="settings-label">自己紹介 (短め・ホーム用)</label>
                    <textarea
                        className="login-input"
                        value={bio}
                        onChange={(e) => setBio(e.target.value)}
                        placeholder="一行から二行程度の自己紹介..."
                        rows={2}
                        style={{ marginBottom: 16, resize: "vertical", fontFamily: "var(--sans)" }}
                    />

                    <label className="settings-label">About me (長めのプロフ・詳細用)</label>
                    <textarea
                        className="login-input"
                        value={aboutMe}
                        onChange={(e) => setAboutMe(e.target.value)}
                        placeholder="自分について詳しく紹介してください..."
                        rows={6}
                        style={{ marginBottom: 16, resize: "vertical", fontFamily: "var(--sans)" }}
                    />
                </section>

                {/* SNSリンク */}
                <section style={{ marginBottom: 40 }}>
                    <h2 className="settings-section-title">リンク / SNS</h2>
                    <p style={{ fontSize: 12, color: "var(--text-soft)", marginBottom: 16 }}>
                        URLを入力するとあなたのプロフィールに表示されます。クリックで新しいタブで開きます。
                    </p>

                    {links.map((link, i) => (
                        <div key={i} style={{ display: "flex", gap: 8, marginBottom: 10, alignItems: "center" }}>
                            <input
                                type="text"
                                className="login-input"
                                value={link.label}
                                onChange={(e) => updateLink(i, "label", e.target.value)}
                                placeholder="名前 (例: GitHub)"
                                style={{ flex: "0 0 120px", marginBottom: 0 }}
                            />
                            <input
                                type="url"
                                className="login-input"
                                value={link.url}
                                onChange={(e) => updateLink(i, "url", e.target.value)}
                                placeholder="https://..."
                                style={{ flex: 1, marginBottom: 0 }}
                            />
                            <button
                                type="button"
                                onClick={() => removeLink(i)}
                                style={{
                                    background: "none", border: "none", color: "#c44", cursor: "pointer",
                                    fontSize: 18, padding: "4px 8px", flexShrink: 0,
                                }}
                            >
                                ×
                            </button>
                        </div>
                    ))}

                    <button
                        type="button"
                        className="editor-btn editor-btn-secondary"
                        onClick={addLink}
                        disabled={links.length >= 5}
                        style={{ width: "100%", marginTop: 4 }}
                    >
                        {links.length >= 5 ? "最大5つまで" : `＋ リンクを追加 (${links.length}/5)`}
                    </button>
                </section>

                {/* おすすめ設定 */}
                <section style={{ marginBottom: 40 }}>
                    <h2 className="settings-section-title">おすすめの表示設定</h2>
                    <p style={{ fontSize: 13, color: "var(--text-soft)", marginBottom: 16 }}>
                        あなたのプロフィールに「おすすめ」として表示されるコンテンツを選べます。<br />
                        （最大5つまで）
                    </p>
                    <div style={{ display: "flex", flexDirection: "column", gap: 12 }}>
                        {myPosts.filter((p: any) => !!p.published).map(p => {
                            const isProduct = p.tags?.includes("product");
                            return (
                                <div key={p.id} style={{ display: "flex", alignItems: "center", justifyContent: "space-between", padding: "12px 16px", background: "var(--bg-card)", border: "1px solid var(--border)", borderRadius: 8 }}>
                                    <div style={{ flex: 1, display: "flex", alignItems: "center", gap: 8 }}>
                                        <span style={{ fontSize: 11, padding: "2px 6px", borderRadius: 4, background: isProduct ? "var(--azuki-light)" : "#888", color: "#fff" }}>
                                            {isProduct ? "Product" : "Blog"}
                                        </span>
                                        <span style={{ fontSize: 14, fontWeight: 500 }}>{p.title}</span>
                                    </div>
                                    <label style={{ display: "flex", alignItems: "center", gap: 6, cursor: "pointer" }}>
                                        <span style={{ fontSize: 12, color: "var(--text-soft)" }}>おすすめ表示</span>
                                        <input
                                            type="checkbox"
                                            checked={!!p.pinned}
                                            onChange={() => togglePinned(p)}
                                        />
                                    </label>
                                </div>
                            );
                        })}
                        {myPosts.filter((p: any) => !!p.published).length === 0 && (
                            <p style={{ fontSize: 13, color: "var(--text-soft)" }}>公開済みの記事やプロダクトがありません。</p>
                        )}
                    </div>
                </section>

                {/* 保存 */}
                <button
                    className="editor-btn editor-btn-primary"
                    onClick={handleSave}
                    disabled={saving}
                    style={{ width: "100%", marginBottom: 48, padding: "12px 0" }}
                >
                    {saving ? "保存中..." : "設定を保存"}
                </button>

                {/* データとバックアップ */}
                <section style={{ borderTop: "1px solid var(--border)", paddingTop: 32, paddingBottom: 32 }}>
                    <h2 className="settings-section-title">データとバックアップ</h2>
                    <p style={{ fontSize: 13, color: "var(--text-soft)", marginBottom: 16 }}>
                        あなたの設定、自己紹介、そしてこれまで投稿したすべての記事やプロダクトのデータをJSON形式でエクスポート（ダウンロード）できます。
                    </p>
                    <a
                        href="/api/user/export"
                        download
                        className="editor-btn editor-btn-secondary"
                        style={{ display: "inline-block", textAlign: "center", textDecoration: "none" }}
                    >
                        データをエクスポート
                    </a>
                </section>

                {/* 危険ゾーン */}
                <section style={{ borderTop: "1px solid var(--border)", paddingTop: 32 }}>
                    <h2 className="settings-section-title" style={{ color: "#c44" }}>危険な操作</h2>

                    <button
                        className="editor-btn editor-btn-secondary"
                        onClick={() => signOut({ callbackUrl: "/" })}
                        style={{ width: "100%", marginBottom: 12 }}
                    >
                        ログアウト
                    </button>

                    <button
                        className="editor-btn editor-btn-danger"
                        onClick={handleDeleteAccount}
                        style={{ width: "100%" }}
                    >
                        アカウントを削除
                    </button>
                    <p style={{ fontSize: 11, color: "#c44", marginTop: 8 }}>
                        アカウントを削除すると、すべての投稿データも完全に削除されます。
                    </p>
                </section>
            </div>
        </>
    );
}

"use client";

import { useEffect, useState, useRef, useCallback } from "react";
import { useSession } from "next-auth/react";
import { useParams } from "next/navigation";
import Link from "next/link";

/* ─── Types ─── */
interface Post {
    id: string;
    title: string;
    content?: string;
    excerpt?: string;
    headerImage?: string;
    tags: string[];
    published?: boolean;
    createdAt: string;
    slug?: string;
    date?: string;
}

interface SocialLink {
    label: string;
    url: string;
}

interface UserProfile {
    id: string;
    name: string;
    email: string;
    image?: string | null;
    headerImage?: string | null;
    bio: string;
    aboutMe: string;
    links: SocialLink[];
    posts: Post[];
}

/* ─── Tag colors ─── */
const TAG_COLORS: Record<string, string> = {
    "AI": "#d4877a", "思考": "#8a7a6b", "コード": "#6b7a8a",
    "3D": "#8a6b7a", "哲学": "#7a6b8a", "テクノロジー": "#d4877a",
    "社会": "#6b8a7a", "作品": "#7a8a6b", "ツール": "#6b8a8a",
    "デザイン": "#8a6b6b", "言語": "#6b6b8a",
};

function Tag({ label }: { label: string }) {
    const c = TAG_COLORS[label] || "#9b6b6b";
    return (
        <span className="tag" style={{ color: c, background: c + "18", border: `1px solid ${c}30` }}>
            {label}
        </span>
    );
}

function fmtDate(d: string) {
    if (!d) return "";
    try {
        const date = new Date(d);
        return new Intl.DateTimeFormat("ja-JP", {
            timeZone: "Asia/Tokyo",
            year: "numeric",
            month: "2-digit",
            day: "2-digit",
        }).format(date).replace(/\//g, ".");
    } catch {
        return d.substring(0, 10).replace(/-/g, ".");
    }
}

function shuffle<T>(arr: T[]): T[] {
    const a = [...arr];
    for (let i = a.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [a[i], a[j]] = [a[j], a[i]];
    }
    return a;
}

export default function UserPage() {
    const { data: session } = useSession();
    const params = useParams();
    const userName = params.name as string;

    const [user, setUser] = useState<UserProfile | null>(null);
    const [posts, setPosts] = useState<Post[]>([]);
    const [products, setProducts] = useState<Post[]>([]);
    const [loading, setLoading] = useState(true);
    const [overlayOpen, setOverlayOpen] = useState(false);
    const [overlayContent, setOverlayContent] = useState("");
    const [overlayMeta, setOverlayMeta] = useState<{ date: string; tags: string[] }>({ date: "", tags: [] });

    // Blog pagination
    const BLOG_PER_PAGE = 7;
    const [blogPage, setBlogPage] = useState(0);

    const sectionsRef = useRef<HTMLElement[]>([]);

    /* ─── Fetch user data ─── */
    useEffect(() => {
        async function loadUser() {
            try {
                const res = await fetch(`/api/user/${userName}`);
                if (res.ok) {
                    const data: UserProfile = await res.json();
                    setUser(data);
                    const allPosts = data.posts.map((p) => ({
                        ...p,
                        date: p.date || p.createdAt,
                        excerpt: p.excerpt || (p.content ? p.content.replace(/<[^>]*>/g, "").substring(0, 100) + "..." : ""),
                    }));
                    setPosts(allPosts.filter((p) => !p.tags?.includes("product")));
                    setProducts(allPosts.filter((p) => p.tags?.includes("product")));
                }
            } catch (e) {
                console.warn("Failed to load user:", e);
            } finally {
                setLoading(false);
            }
        }
        if (userName) loadUser();
    }, [userName]);

    /* ─── Scroll tracking ─── */
    const SECTIONS = ["home", "blog", "product", "about"];

    const updateNav = useCallback(() => {
        const sy = window.scrollY + 120;
        let cur = "home";
        SECTIONS.forEach((id) => {
            const el = document.getElementById(id);
            if (el && el.offsetTop <= sy) cur = id;
        });
        document.querySelectorAll(".nav-link").forEach((link) => {
            const el = link as HTMLElement;
            el.classList.toggle("active", el.dataset.section === cur);
        });
        document.querySelectorAll(".page-dot").forEach((dot) => {
            const el = dot as HTMLElement;
            const isActive = el.dataset.section === cur;
            el.classList.toggle("active", isActive);
            el.style.width = isActive ? "20px" : "6px";
        });
        const nb = document.getElementById("navbar");
        if (nb) nb.classList.toggle("scrolled", window.scrollY > 10);
    }, []);

    useEffect(() => {
        window.addEventListener("scroll", updateNav, { passive: true });
        updateNav();
        return () => window.removeEventListener("scroll", updateNav);
    }, [updateNav]);

    /* ─── Intersection Observer ─── */
    useEffect(() => {
        const obs = new IntersectionObserver(
            (entries) => {
                entries.forEach((entry) => {
                    if (entry.isIntersecting) {
                        entry.target.classList.add("visible");
                        requestAnimationFrame(() => {
                            const items = entry.target.querySelectorAll(".fade-item:not(.show)");
                            items.forEach((el, i) => {
                                setTimeout(() => el.classList.add("show"), i * 70);
                            });
                        });
                    }
                });
            },
            { threshold: 0.08 }
        );
        sectionsRef.current.forEach((s) => { if (s) obs.observe(s); });
        return () => obs.disconnect();
    }, [posts, products, user]);

    /* ─── Overlay ─── */
    const openPost = (post: Post) => {
        setOverlayMeta({ date: fmtDate(post.date || post.createdAt), tags: post.tags || [] });
        setOverlayOpen(true);
        document.body.style.overflow = "hidden";
        setOverlayContent(post.content || "<p style='color:var(--text-soft)'>記事の内容がありません。</p>");
    };

    const closeOverlay = () => {
        setOverlayOpen(false);
        document.body.style.overflow = "";
    };

    useEffect(() => {
        const handler = (e: KeyboardEvent) => { if (e.key === "Escape") closeOverlay(); };
        document.addEventListener("keydown", handler);
        return () => document.removeEventListener("keydown", handler);
    }, []);

    /* ─── Computed ─── */
    const blogTotalPages = Math.ceil(posts.length / BLOG_PER_PAGE);
    const blogItems = posts.slice(blogPage * BLOG_PER_PAGE, (blogPage + 1) * BLOG_PER_PAGE);
    // おすすめの抽出
    const pinnedPosts = posts.filter((p: any) => p.pinned);

    // 足りない分は新しい順から補填する
    let recommendPosts = [...pinnedPosts];
    if (recommendPosts.length < 5) {
        const unpinnedPosts = posts.filter((p: any) => !p.pinned);
        recommendPosts = [...recommendPosts, ...unpinnedPosts.slice(0, 5 - recommendPosts.length)];
    }

    const scrollTo = (id: string) => {
        const el = document.getElementById(id);
        if (el) el.scrollIntoView({ behavior: "smooth" });
    };

    if (loading) {
        return (
            <div className="login-container">
                <div className="login-card" style={{ textAlign: "center" }}>
                    <p style={{ color: "var(--text-soft)" }}>読み込み中...</p>
                </div>
            </div>
        );
    }

    if (!user) {
        return (
            <div className="login-container">
                <div className="login-card" style={{ textAlign: "center" }}>
                    <h2 style={{ fontFamily: "var(--serif)", marginBottom: 16 }}>ユーザーが見つかりません</h2>
                    <Link href="/" style={{ color: "var(--azuki)" }}>ホームに戻る</Link>
                </div>
            </div>
        );
    }

    const displayName = user.name || userName;

    return (
        <>
            {/* ─── Navbar ─── */}
            <nav className="navbar" id="navbar">
                <Link href="/" className="nav-logo" style={{ textDecoration: "none" }}>
                    <img src="/images/a.png" alt="Next Blog" className="nav-logo-img" />
                    Next Blog
                </Link>
                <div className="nav-links">
                    <button className="nav-link active" data-section="home" onClick={() => scrollTo("home")}>
                        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.8" strokeLinecap="round" strokeLinejoin="round"><path d="M3 9l9-7 9 7v11a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2z" /><polyline points="9 22 9 12 15 12 15 22" /></svg>
                        Home
                    </button>
                    <button className="nav-link" data-section="blog" onClick={() => scrollTo("blog")}>
                        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.8" strokeLinecap="round" strokeLinejoin="round"><path d="M2 3h6a4 4 0 0 1 4 4v14a3 3 0 0 0-3-3H2z" /><path d="M22 3h-6a4 4 0 0 0-4 4v14a3 3 0 0 1 3-3h7z" /></svg>
                        Blog
                    </button>
                    <button className="nav-link" data-section="product" onClick={() => scrollTo("product")}>
                        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.8" strokeLinecap="round" strokeLinejoin="round"><path d="M21 16V8a2 2 0 0 0-1-1.73l-7-4a2 2 0 0 0-2 0l-7 4A2 2 0 0 0 3 8v8a2 2 0 0 0 1 1.73l7 4a2 2 0 0 0 2 0l7-4A2 2 0 0 0 21 16z" /></svg>
                        Product
                    </button>
                    <button className="nav-link" data-section="about" onClick={() => scrollTo("about")}>
                        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.8" strokeLinecap="round" strokeLinejoin="round"><path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2" /><circle cx="12" cy="7" r="4" /></svg>
                        About me
                    </button>
                </div>
                <div className="nav-auth">
                    {session ? (
                        <>
                            <Link href="/editor" className="nav-auth-btn nav-write-btn">✏ 記事を書く</Link>
                            <Link href="/settings" className="nav-auth-btn nav-user-btn" style={{ textDecoration: "none" }}>⚙</Link>
                        </>
                    ) : (
                        <Link href="/login" className="nav-auth-btn nav-login-btn">ログイン</Link>
                    )}
                </div>
            </nav>

            {/* ─── Page dots ─── */}
            <div className="page-dots" id="pageDots">
                {SECTIONS.map((s) => (
                    <div key={s} className={`page-dot ${s === "home" ? "active" : ""}`} style={{ width: s === "home" ? 20 : 6 }} data-section={s} />
                ))}
            </div>

            {/* ─── Main content ─── */}
            <div className="main-content">

                {/* ──── HOME ──── */}
                <section className="section visible" id="home" ref={(el) => { if (el) sectionsRef.current[0] = el; }}>
                    <div className="hero">
                        <div className="hero-bg" style={user.headerImage ? { backgroundImage: `url(${user.headerImage})`, backgroundSize: "cover", backgroundPosition: "center" } : undefined} />
                        <div className="hero-content" style={user.headerImage ? { position: "relative", zIndex: 1 } : undefined}>
                            <h1 style={user.headerImage ? { color: "#fff", textShadow: "0 2px 10px rgba(0,0,0,0.7)" } : undefined}>{displayName} Blog</h1>
                            <p style={user.headerImage ? { color: "#eee", textShadow: "0 1px 5px rgba(0,0,0,0.7)" } : undefined}>{user.bio || "学び、作り、考える。日々の記録。"}</p>
                        </div>
                    </div>
                    {recommendPosts.length > 0 && (
                        <>
                            <h2 className="section-title fade-item">おすすめ</h2>
                            <div className="recommend-row">
                                {recommendPosts.map((p: any) => (
                                    <div key={p.id} className="card card-sm fade-item" style={{ padding: 18 }} onClick={() => openPost(p)}>
                                        <div style={{ display: "flex", alignItems: "center", gap: 10, marginBottom: 6, flexWrap: "wrap" }}>
                                            <h4 style={{ fontSize: 14, fontWeight: 600, lineHeight: 1.5, margin: 0 }}>{p.title}</h4>
                                            <div style={{ display: "flex", gap: 4, flexWrap: "wrap" }}>
                                                {(p.tags || []).filter((t: any) => t !== "product").map((t: any) => <Tag key={t} label={t} />)}
                                            </div>
                                        </div>
                                        <p style={{ fontSize: 12, color: "var(--azuki-light)" }}>{fmtDate(p.date || p.createdAt)}</p>
                                    </div>
                                ))}
                            </div>
                        </>
                    )}
                </section>

                <div className="section-divider" />

                {/* ──── BLOG ──── */}
                <section className="section" id="blog" ref={(el) => { if (el) sectionsRef.current[1] = el; }}>
                    <h2 className="section-title">Blog</h2>
                    {posts.length === 0 ? (
                        <p style={{ textAlign: "center", color: "var(--text-soft)", padding: "40px 0" }}>まだ記事がありません。</p>
                    ) : (
                        <div className="blog-list">
                            {blogItems.map((p, i) => (
                                <div key={p.id} className="blog-item fade-item" style={{ transitionDelay: `${i * 60}ms` }} onClick={() => openPost(p)}>
                                    <div style={{ flex: 1 }}>
                                        <div style={{ display: "flex", alignItems: "center", gap: 12, marginBottom: 6, flexWrap: "wrap" }}>
                                            <h3 style={{ margin: 0 }}>{p.title}</h3>
                                            <div style={{ display: "flex", gap: 6, flexWrap: "wrap" }}>
                                                {(p.tags || []).filter(t => t !== "product").map((t) => <Tag key={t} label={t} />)}
                                            </div>
                                        </div>
                                        <p>{p.excerpt}</p>
                                    </div>
                                    <div className="blog-date">{fmtDate(p.date || p.createdAt)}</div>
                                </div>
                            ))}
                        </div>
                    )}
                    {blogTotalPages > 1 && (
                        <div className="pagination">
                            <button className="page-btn" onClick={() => setBlogPage(Math.max(0, blogPage - 1))} disabled={blogPage === 0}>
                                <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2"><polyline points="15 18 9 12 15 6" /></svg>
                            </button>
                            <span className="page-info">{blogPage + 1} / {blogTotalPages}</span>
                            <button className="page-btn" onClick={() => setBlogPage(Math.min(blogTotalPages - 1, blogPage + 1))} disabled={blogPage === blogTotalPages - 1}>
                                <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2"><polyline points="9 18 15 12 9 6" /></svg>
                            </button>
                        </div>
                    )}
                </section>

                <div className="section-divider" />

                {/* ──── PRODUCT ──── */}
                <section className="section" id="product" ref={(el) => { if (el) sectionsRef.current[2] = el; }}>
                    <h2 className="section-title">Product</h2>
                    {products.length === 0 ? (
                        <p style={{ textAlign: "center", color: "var(--text-soft)", padding: "40px 0" }}>まだプロダクトがありません。</p>
                    ) : (
                        <div className="product-grid">
                            {products.map((p) => (
                                <div key={p.id} className="product-card fade-item" onClick={() => openPost(p)}>
                                    {p.headerImage ? (
                                        <img src={p.headerImage} alt={p.title} style={{ width: "100%", height: 80, objectFit: "cover", borderRadius: 10, marginBottom: 8 }} />
                                    ) : (
                                        <div className="product-thumb" style={{ background: "linear-gradient(135deg,#e8d5d0,#e8d5d088)" }}>
                                            <div className="product-thumb-inner" />
                                        </div>
                                    )}
                                    <div className="product-info">
                                        <h3>{p.title}</h3>
                                        <p>{p.excerpt}</p>
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}
                </section>

                <div className="section-divider" />

                {/* ──── ABOUT ──── */}
                <section className="section" id="about" ref={(el) => { if (el) sectionsRef.current[3] = el; }}>
                    <h2 className="section-title">About me</h2>
                    <div className="about-header">
                        <div className="about-avatar" style={{ width: 72, height: 72, borderRadius: "50%", background: "var(--bg-soft)", display: "flex", alignItems: "center", justifyContent: "center", fontSize: 28, color: "var(--azuki)", overflow: "hidden", border: "1px solid var(--border)" }}>
                            {user.image ? (
                                <img src={user.image} alt="Avatar" style={{ width: "100%", height: "100%", objectFit: "cover" }} />
                            ) : (
                                displayName.charAt(0).toUpperCase()
                            )}
                        </div>
                        <div>
                            <h3 className="about-name">{displayName}</h3>
                        </div>
                    </div>
                    {user.aboutMe && (
                        <div className="about-bio" style={{ whiteSpace: "pre-wrap" }}>
                            <p>{user.aboutMe}</p>
                        </div>
                    )}

                    {/* SNS / Contact Links */}
                    {user.links && user.links.length > 0 && (
                        <div className="contact-section" style={{ marginTop: 32 }}>
                            <h3 className="contact-title">Links</h3>
                            <div className="contact-links">
                                {user.links.map((link, i) => {
                                    const isEmail = link.label.toLowerCase() === "email" || link.label === "メール";
                                    const rawUrl = isEmail && !link.url.startsWith("mailto:") ? `mailto:${link.url}` : link.url;
                                    const displayUrl = link.url.replace(/^https?:\/\//, "").replace(/^mailto:/, "");

                                    return (
                                        <div key={i} style={{ display: "flex", gap: 8, alignItems: "center" }}>
                                            <a href={rawUrl} target="_blank" rel="noopener noreferrer" className="contact-link" style={{ flex: 1, marginBottom: 0 }}>
                                                <div className="contact-link-inner">
                                                    <div className="contact-icon" style={{ background: "rgba(155,107,107,0.08)", color: "var(--azuki)" }}>
                                                        {link.label.charAt(0).toUpperCase()}
                                                    </div>
                                                    <div>
                                                        <div className="contact-name">{link.label}</div>
                                                        <div className="contact-handle">{displayUrl}</div>
                                                    </div>
                                                </div>
                                                <svg width="14" height="14" viewBox="0 0 24 24" fill="none" stroke="var(--azuki-light)" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><path d="M18 13v6a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V8a2 2 0 0 1 2-2h6" /><polyline points="15 3 21 3 21 9" /><line x1="10" y1="14" x2="21" y2="3" /></svg>
                                            </a>
                                            <button
                                                onClick={(e) => {
                                                    e.preventDefault();
                                                    navigator.clipboard.writeText(isEmail ? displayUrl : link.url);
                                                    alert(`${link.label} のリンクをコピーしました！`);
                                                }}
                                                className="editor-btn editor-btn-secondary"
                                                style={{ height: "100%", padding: "0 12px", border: "1px solid var(--border)", background: "var(--bg-card)", cursor: "pointer", borderRadius: 12, display: "flex", alignItems: "center" }}
                                                title="リンクをコピー"
                                            >
                                                <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="var(--azuki)" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><rect x="9" y="9" width="13" height="13" rx="2" ry="2" /><path d="M5 15H4a2 2 0 0 1-2-2V4a2 2 0 0 1 2-2h9a2 2 0 0 1 2 2v1" /></svg>
                                            </button>
                                        </div>
                                    );
                                })}
                            </div>
                        </div>
                    )}
                </section>
            </div>

            {/* ─── Footer ─── */}
            <footer className="footer">
                <span className="footer-copy">© 2026 Next Blog</span>
            </footer>

            {/* ─── Overlay ─── */}
            <div className={`post-overlay ${overlayOpen ? "open" : ""}`} onClick={closeOverlay}>
                <div className="post-panel" onClick={(e) => e.stopPropagation()}>
                    <div className="post-panel-header">
                        <span style={{ fontSize: 13, color: "var(--text-soft)" }}>{overlayMeta.date}</span>
                        <button className="post-close-btn" onClick={closeOverlay}>
                            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2"><line x1="18" y1="6" x2="6" y2="18" /><line x1="6" y1="6" x2="18" y2="18" /></svg>
                        </button>
                    </div>
                    <div className="post-panel-body">
                        <div className="post-meta">
                            {overlayMeta.tags.filter(t => t !== "product").map((t) => <Tag key={t} label={t} />)}
                        </div>
                        <div className="md-content" dangerouslySetInnerHTML={{ __html: overlayContent }} />
                    </div>
                </div>
            </div>
        </>
    );
}

"use client";

import { useSession } from "next-auth/react";
import Link from "next/link";

export default function Navbar() {
    const { data: session } = useSession();

    const scrollTo = (id: string) => {
        const el = document.getElementById(id);
        if (el) el.scrollIntoView({ behavior: "smooth" });
    };

    return (
        <nav className="navbar" id="navbar">
            <button className="nav-logo" onClick={() => scrollTo("home")}>
                <img src="/images/a.png" alt="Next Blog" className="nav-logo-img" />
                Next Blog
            </button>
            <div className="nav-links">
                <button className="nav-link active" data-section="home" onClick={() => scrollTo("home")}>
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.8" strokeLinecap="round" strokeLinejoin="round"><path d="M3 9l9-7 9 7v11a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2z" /><polyline points="9 22 9 12 15 12 15 22" /></svg>
                    Home
                </button>
                <button className="nav-link" data-section="blog" onClick={() => scrollTo("blog")}>
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.8" strokeLinecap="round" strokeLinejoin="round"><path d="M2 3h6a4 4 0 0 1 4 4v14a3 3 0 0 0-3-3H2z" /><path d="M22 3h-6a4 4 0 0 0-4 4v14a3 3 0 0 1 3-3h7z" /></svg>
                    Blog
                </button>
                <button className="nav-link" data-section="product" onClick={() => scrollTo("product")}>
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.8" strokeLinecap="round" strokeLinejoin="round"><path d="M21 16V8a2 2 0 0 0-1-1.73l-7-4a2 2 0 0 0-2 0l-7 4A2 2 0 0 0 3 8v8a2 2 0 0 0 1 1.73l7 4a2 2 0 0 0 2 0l7-4A2 2 0 0 0 21 16z" /></svg>
                    Product
                </button>
            </div>
            <div className="nav-auth">
                {session ? (
                    <>
                        <Link href="/editor" className="nav-auth-btn nav-write-btn">
                            ✏ 記事を書く
                        </Link>
                        <Link href="/settings" className="nav-auth-btn nav-user-btn" style={{ textDecoration: "none" }}>
                            ⚙
                        </Link>
                    </>
                ) : (
                    <Link href="/login" className="nav-auth-btn nav-login-btn">
                        ログイン
                    </Link>
                )}
            </div>
        </nav>
    );
}

